import React, { useState } from "react";

// 각 탭의 컴포넌트
const Tab1: React.FC = () => <div>Tab 1 Content</div>;
const Tab2: React.FC = () => <div>Tab 2 Content</div>;
const Tab3: React.FC = () => <div>Tab 3 Content</div>;
const Tab4: React.FC = () => <div>Tab 4 Content</div>;
const Tab5: React.FC = () => <div>Tab 5 Content</div>;
const Tab6: React.FC = () => <div>Tab 6 Content</div>;

const Sidebar: React.FC = () => {
    // 선택된 탭의 인덱스를 관리하기 위한 상태 변수
    const [activeTabIndex, setActiveTabIndex] = useState(0); // 초기값은 첫 번째 탭

    // 선택된 탭에 따라 활성화할 컴포넌트
    const tabs = [<Tab1 />, <Tab2 />, <Tab3 />, <Tab4 />, <Tab5 />, <Tab6 />];
    const activeContent = tabs[activeTabIndex];

    // 탭을 선택할 때 활성화 상태를 업데이트하는 이벤트 핸들러
    const handleTabClick = (tabIndex: number) => {
        setActiveTabIndex(tabIndex);
    };

    return (
        <div>
            <ul className="sidebar">
                <li className={activeTabIndex === 0 ? "active" : ""} onClick={() => handleTabClick(0)}>
                    Tab 1
                </li>
                <li className={activeTabIndex === 1 ? "active" : ""} onClick={() => handleTabClick(1)}>
                    Tab 2
                </li>
                <li className={activeTabIndex === 2 ? "active" : ""} onClick={() => handleTabClick(2)}>
                    Tab 3
                </li>
                <li className={activeTabIndex === 3 ? "active" : ""} onClick={() => handleTabClick(3)}>
                    Tab 4
                </li>
                <li className={activeTabIndex === 4 ? "active" : ""} onClick={() => handleTabClick(4)}>
                    Tab 5
                </li>
                <li className={activeTabIndex === 5 ? "active" : ""} onClick={() => handleTabClick(5)}>
                    Tab 6
                </li>
            </ul>
            <div className="content">{activeContent}</div>
        </div>
    );
};
