import React, { CSSProperties, useState } from "react";
import { Link } from "react-router-dom";
import Select from "react-select";
import Stack from "@mui/material/Stack";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import "./UpdateInfo.css";

type InfoChangeProps = {
    onSubmit: (form: { name: string; birth: number; company: string; region: string }) => void;
};

// { onSubmit }: InfoChangeProps

interface InfoChange {
    onSubmit: (form: { name: string; birth: number; company: string; region: string }) => void;
}

function UpdateInfo() {
    function onSubmit(form: { name: string; birth: number; company: string; region: string }) {
        return form;
    }

    type OptionType = {
        value: string;
        label: string;
    };

    const options: OptionType[] = [
        { value: "1999", label: "1999년" },
        { value: "2000", label: "2000년" },
        { value: "2001", label: "2001년" },
        { value: "2002", label: "2002년" },
        { value: "2003", label: "2003년" },
        { value: "2004", label: "2004년" },
        { value: "2005", label: "2005년" },
    ];

    //여기는 내가 적은 코드

    const MyComponent = () => {
        <Select options={options} />;
    };

    // var state = {
    //     selectedOptions: null,
    // }
    // const {selectedOption} = this.state;
    function handleChange() {}

    const comfirmBtn: CSSProperties = {
        marginTop: "30px",
        // paddingLeft: '30px',
        display: "flex",
        justifyContent: "center",
    };

    const mainRightDiv: CSSProperties = {
        display: "flex",
        marginBottom: "100px",
    };

    const userIcon: CSSProperties = {
        fontSize: "4rem",
        fontWeight: "lightweight",
        borderColor: "gray",
        marginLeft: "50px",
        marginRight: "20px",
        marginBottom: "20px",
    };

    //메인 박스 스타일
    const mainBoxStyle: CSSProperties = {
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        // backgroundColor:'orange',
        height: "50vh",
        paddingTop: "100px",
        paddingBottom: "60px",
        /*
        display: 'flex',
        justifyContent: 'center',
        align-items: 'center,
        min-height: '100vh',
        */
    };

    //입력 박스 스타일
    const inputBoxStyle: CSSProperties = {
        width: "300px",
        height: "40px",
        marginLeft: "25px",
        marginBottom: "30px",
        marginTop: "-2px",
    };

    //입력 박스 옆 텍스트
    const inputTextStyle: CSSProperties = {
        fontSize: "25px",
        display: "flex",
        flexDirection: "column",
        justifyContent: "space-evenly",
        alignContent: "space-evenly",
    };

    //가로선 스타일
    const hrStyle: CSSProperties = {
        border: "1px solid black",
        width: "700px",
        marginTop: "30px",
        // border: solid 10px red
    };

    //선택 옵션 리스트 스타일
    const optionBoxStyle: CSSProperties = {
        // display: 'flex',
        // flex: 1,
        // flexDirection: 'row',
        // alignItems: 'center',
        // justifyContent: 'space-evenly',
        height: "40px",
        width: "5rem",
        // margin: '10px',
        // borderRadius: '10px',
        display: "inline-block",
    };

    const optionBox: CSSProperties = {
        width: "100%",
        textAlign: "center",
    };

    const birthBox: CSSProperties = {
        marginBottom: "30px",
    };

    const footer: CSSProperties = {
        display: "flex",
        justifyContent: "center",
    };

    const footerBoxStyle: CSSProperties = {
        width: "20%",
        height: "50px",
        margin: "50px",
        borderRadius: "10px",
    };

    const delText: CSSProperties = {
        color: "red",
    };
    //css 끝

    const [form, setForm] = useState({
        name: "",
        birth: 0,
        company: "",
        region: "",
    });

    const { name, birth, company, region } = form;

    const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setForm({
            ...form,
            [name]: value,
        });
    };

    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        onSubmit(form);
        console.log(form);
        setForm({
            name: "",
            birth: 0,
            company: "",
            region: "",
        }); // 초기화
    };

    return (
        <form onSubmit={handleSubmit} className="body">
            <header>Tempo_UpdateInfo</header>

            <section className="mainScreen">
                <h1>TEMPO</h1>
                <div className="inputsection">
                    <div className="textDiv">
                        <h2 className="texts">프로필 사진</h2>
                        <h2 className="texts">이름</h2>
                        <h2 className="texts">생년월일</h2>
                        <h2 className="texts">소속</h2>
                        <h2 className="texts">거주지</h2>
                    </div>
                    <div className="mainRightDiv">
                        <div style={inputBoxStyle}>
                            <section>
                                <i className="fas fa-user-edit" style={userIcon}></i>
                                <button className="boxStyle" type="submit">
                                    내PC에서 찾기
                                </button>
                            </section>
                            <TextField
                                id="outlined-basic"
                                label="name"
                                variant="outlined"
                                style={inputBoxStyle}
                                type="text"
                                name="name"
                                value={name}
                                onChange={onChange}
                            />
                            <br />
                            <TextField
                                id="outlined-basic"
                                label="birth(ex.19991127)"
                                variant="outlined"
                                style={inputBoxStyle}
                                type="text"
                                name="birth"
                                value={birth}
                                onChange={onChange}
                            />
                            <br />
                            <TextField
                                id="outlined-basic"
                                label="company"
                                variant="outlined"
                                style={inputBoxStyle}
                                type="text"
                                name="company"
                                value={company}
                                onChange={onChange}
                            />
                            <br />
                            <TextField
                                id="outlined-basic"
                                label="region"
                                variant="outlined"
                                style={inputBoxStyle}
                                type="text"
                                name="region"
                                value={region}
                                onChange={onChange}
                            />
                        </div>
                    </div>
                </div>
            </section>
            <hr className="hrStyle"></hr>
            <section className="navsection">
                <div className="navBtnStyle">
                    <Link to="/">
                        <Button variant="outlined" className="boxStyle">
                            변경
                        </Button>
                    </Link>
                    <Link to="/myForm">
                        <Button variant="outlined" className="boxStyle">
                            취소
                        </Button>
                    </Link>
                </div>
            </section>
            <div className="comfirmBtn">
                <Stack spacing={2} direction="row">
                    <Button variant="outlined" type="submit" className="boxStyle2">
                        확인
                    </Button>
                    <Button variant="outlined" color="error" className="boxStyle2">
                        계정 삭제
                    </Button>
                </Stack>
            </div>
        </form>
    );
}

export default UpdateInfo;
